                    bB -> bC
                    bC -> bC
                    bD -> bD
                }
            }

    fun Button.endButton(listTurns: List<Turn>): Button {
        var endButton = this
        for (i in listTurns) {
            endButton = nextButton(endButton, i)
        }
        return endButton
    }

